<script>
export default {
  computed: {
    user() {
      return this.$auth.user
    },
  },

  mounted() {
    if (this.$cookies.get('token') === null) {
      this.logout()
    } else {
      this.$axios
        .get('/api/admin/profile')
        .then((response) => {
          if (response.status_code == 401) {
            this.logout()
          }
        })
        .catch((err) => {
          if (err.response.status == 401) {
            this.logout()
          }
        })
    }
  },

  methods: {
    async logout() {
      //logout auth
      await this.$auth.logout()

      //remove cookies
      this.$cookies.removeAll()

      //redirect route login
      this.$router.push({
        name: 'login',
      })
    },
  },
}
</script>

